// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155Holder.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./libs/Strings.sol";

contract Quantized is ERC1155, ERC1155Holder, Pausable, Ownable {
    using SafeMath for uint256;
    using Strings for string;

    mapping(uint256 => uint256) private totalSupplies;

    /**
     * @dev Emitted when an erc20 token is quantized (erc20 tokens tranferred to contract, erc1155 minted and transferred to quantizer)
     */
    event Quantized(address indexed quantizer, address indexed token, uint256 amount);

    /**
     * @dev Emitted when an erc20 token is fungized (erc1155 burned and erc20 tokens returned)
     */
    event Dequantized(address indexed dequantizer, address indexed token, uint256 amount);

    /**
     * @dev Modifier to make a function callable only when the caller is owner or sender
     *
     * Requirements:
     *
     * - The caller is owner or sender of this token.
     */
    modifier ownerSenderOnly(address account) {
        require(_isOwner(account) || _isSender(account), "QuantizedERC20: caller is not owner nor sender");
        _;
    }

    /**
     * @dev Contract initializer.
     */
    constructor() ERC1155("https://metadata.quantizer.finance/metadata/") {
        setApprovalForAll(address(this), true);
    }

    /**
     * @dev Returns true if message sender is the given account
     */
    function _isSender(address account) internal view returns (bool) {
        return _msgSender() == account;
    }

    /**
     * @dev Returns true if account is owner
     */
    function _isOwner(address account) internal view returns (bool) {
        return owner() == account;
    }

    /**
     * @dev Get the total supply for this quantized erc20
     */
    function totalSupply(address _addr) public view returns (uint256) {
        return totalSupplies[uint256(_addr)];
    }

    /**
     * @dev Pause this token. Can only be called by owneradmins
     */
    function pause() public onlyOwner {
        _pause();
    }

    /**
     * @dev Unause this token. Can only be called by owneradmins
     */
    function unpause() public onlyOwner {
        _unpause();
    }

    /**
     * @dev Quantize this token to erc1155.
     */
    function quantize(address token, uint256 amount) external {
        // create unit multiplier
        uint256 multiplier = 10**ERC20(token).decimals();
        // require they have enough erc20 token to mint this erc1155
        require(ERC20(token).balanceOf(_msgSender()) >= amount.mul(multiplier), "Insufficient balance");
        // transfer erc20 to quantizer contract
        ERC20(token).transferFrom(_msgSender(), address(this), amount.mul(multiplier));
        // mint equivalent number of erc1155 tokens
        _mint(_msgSender(), uint256(token), amount, "0x0");
        // emit an event about it
        emit Quantized(_msgSender(), token, amount);
    }

    /**
     * @dev Fungize this token to erc20.
     */
    function dequantize(address token, uint256 amount) external {
        // create unit multiplier
        uint256 multiplier = 10**ERC20(token).decimals();
        // require they have enough erc20 token to mint this erc1155
        require(IERC1155(token).balanceOf(_msgSender(), uint256(token)) >= amount, "Insufficient balance");
        // burn burn the erc1155 tokens
        _burn(_msgSender(), uint256(token), amount);
        // transfer erc20 to fungizer
        ERC20(token).transferFrom(address(this), _msgSender(), amount.mul(multiplier));
        // emit an event about it
        emit Dequantized(_msgSender(), token, amount);
    }

    /**
     * @dev Returns the metadata URI for this token type
     */
    function uri(uint256 _id) public view override(ERC1155) returns (string memory) {
        require(totalSupply(address(_id)) != 0, "QuantizedERC20#uri: NONEXISTENT_TOKEN");
        return Strings.strConcat(ERC1155(this).uri(_id), Strings.uint2str(_id));
    }

    /**
     * @dev run every time a token is tranferred
     */
    function _beforeTokenTransfer(
        address,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory
    ) internal virtual override(ERC1155) {
        for (uint256 idx = 0; idx < ids.length; idx++) {
            uint256 id_x = ids[idx];
            uint256 amount_x = amounts[idx];
            if (from == address(0x0)) {
                // inc total supplies for this token type
                totalSupplies[id_x] = totalSupplies[id_x].add(amount_x);
            } else if (to == address(0x0)) {
                if (totalSupplies[id_x] > amount_x) {
                    totalSupplies[id_x] = totalSupplies[id_x].sub(amount_x);
                }
            }
        }
    }
}

