// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

import "hardhat/console.sol";

contract QuantizedStable is ERC20 {
    using SafeMath for uint256;

    address internal quantizer;

    /**
     * @dev Modifier to make a function callable only by the quantizer
     *
     * Requirements:
     *
     * - The caller is the quantizer address
     */
    modifier quantizerOnly() {
        require(_msgSender() == quantizer, "QuantizedStable: caller is not quantizer");
        _;
    }

    /**
     * @dev Initializes the contract
     */
    constructor(address _quantizer) ERC20("Quantized Stable", "XQS") {
        quantizer = _quantizer;
    }

    /**
     * @dev Mint this amount of tokens
     */
    function mint(address account, uint256 amount) external quantizerOnly returns (uint256) {
        _mint(account, amount);
    }
}

